import React, { useState } from 'react';
// import { useForm } from 'react-hook-form';
import { Stepper, Step, StepLabel, Button } from '@mui/material';

// Import your form component and table component here
import BusinessDetailsForm from './BusinessDetailsForm';
import ReviewBalancesheet from './ReviewBalancesheet';

import UserDataService from './services/user';
import AccountingProviderService from './services/accountingProvider';
import { useAppSelector, useAppDispatch } from './store/hooks';

const steps = ['Fill loan details', 'Review Balancesheet'];

const ApplyLoanStepper = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [balanceSheet, setBalanceSheet] = useState([]);
  const [loanData, setLoanData] = useState([]);
  const userData = useAppSelector((state) => state.user);

  const handleNext = () => {
    if(prevStep)
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

//   const onSubmit = (data) => {
//     console.log(data); // Handle form submission data here
//     handleNext();
//   };

  const getBalanceSheetToReview = (loanDataEntered) => {
    setLoanData(loanDataEntered);
    AccountingProviderService.getBalanceSheet(loanDataEntered.companyTaxId)
    .then((response) => {
      console.log("success", response)
      setBalanceSheet(response.data);
      handleNext();
      // navigate('/home');
    })
    .catch((e) => {
      console.log("error", e);
      const errorMsg = e.response.data.message || 'Something went wrong! Please try again later';
      // setFormError(errorMsg);
      // navigate('/home');
    });
  }

  const submitLoan = () => {
    UserDataService.submitLoan(userData.id, {loanData, balanceSheet})
    .then((response) => {
      console.log("success", response)
      // navigate('/home');
    })
    .catch((e) => {
      console.log("error", e);
      const errorMsg = e.response.data.message || 'Something went wrong! Please try again later';
      // setFormError(errorMsg);
      // navigate('/home');
    });
  }

  const getStepContent = (step) => {
    switch (step) {
      case 0:
        return <BusinessDetailsForm onSubmit={getBalanceSheetToReview} />;
      case 1:
        return <ReviewBalancesheet balanceSheet={balanceSheet} onClickHandler={submitLoan}/>;
      default:
        return null;
    }
  };

  return (
    <div>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <p>All steps completed</p>
          </div>
        ) : (
          <div>
            {getStepContent(activeStep)}
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button disabled={activeStep === 0} onClick={handleBack}>
                Back
              </Button>
              <Button variant="contained" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ApplyLoanStepper;
