import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import UserDataService from '../services/user';
import { setLoans } from '../store/userSlice';

const Home = ({}) => {
  const navigate = useNavigate();
  const dispatch = useAppDispatch();

  const userData = useAppSelector((state) => state.user);
  const loans = userData.loans;

  const goToApplyLoan = () => {
    navigate('/applyLoan');
  };

  React.useEffect(() => {
    if (!userData.isLoggedIn) {
      navigate('/login');
    } else {
      UserDataService.getLoans(userData.id)
      .then((response) => {
        dispatch(setLoans(response.data))
        navigate('/home');
      })
      .catch((e) => {
        console.log("Could not fetch loans")
      });
    }
  }, []);

  return (
    <div>
      {loans.length > 0 ?
        loans.map((loan) => (
          <div key={loan.id} className="snapshot">
            <p className="title">{loan.companyName}</p>
            <div className="content">
              <p><span className='label'>Amount: </span>{loan.loanAmount}</p>
              <p><span className='label'>Accounting Provider: </span>{loan.accountingProvider}</p>
              <p><span className='label'>Probability of approval: </span>{loan.preAssessment}%</p>
            </div>
          </div>
        )) :
        <p>No loans applied yet! Get started <span onClick={goToApplyLoan}>here!</span></p>}
    </div>
  );
};

export default Home;
