import React from 'react';
import { useState } from 'react';
import { useAppDispatch } from '../store/hooks';
import { setSelectedMenu } from '../store/userSlice';

const Sidebar = () => {
  const [activeItemId, setActiveItemId] = useState('');

  const dispatch = useAppDispatch();

  const navItems = [
    {
      title: 'Home',
      itemId: '1',
      onClick: () => dispatch(setSelectedMenu(1))
    },
    {
      title: 'Apply new Business Loan',
      itemId: '2',
      onClick: () => dispatch(setSelectedMenu(2))
    },
  ];
  return (
    <div className="sidebar">
          {navItems.map((item: NavItemProps) => {
            return (
              <div key={item.itemId}
                onClick={() => 
                  {setActiveItemId(item.itemId); item.onClick();}}
                className={`${activeItemId === item.itemId ? 'active' : ''}`}>
                <span>
                  {item.title}
                </span>
              </div>
            );
          })}
    </div>
  );
};

export default Sidebar;
