import React, { useState } from 'react';
import { Stepper, Step, StepLabel } from '@mui/material';

import BusinessDetailsForm from './BusinessDetailsForm';
import ReviewBalancesheet from './ReviewBalancesheet';

import UserDataService from '../services/user';
import AccountingProviderService from '../services/accountingProvider';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import { setCurrentLoanId, setSelectedMenu } from '../store/userSlice';

const steps = ['Fill Business loan details', 'Review Balancesheet'];

const ApplyLoanStepper = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [balanceSheet, setBalanceSheet] = useState([]);
  const [loanData, setLoanData] = useState([]);
  const [errorMsg, setErrorMsg] = useState('');

  const userData = useAppSelector((state) => state.user);
  const currentLoanId = userData.currentLoanId;
  const dispatch = useAppDispatch();

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const getBalanceSheetToReview = (loanDataEntered) => {
    setLoanData(loanDataEntered);
    AccountingProviderService.getBalanceSheet(loanDataEntered.companyTaxId)
    .then((response) => {
      setBalanceSheet(response.data);
      handleNext();
    })
    .catch((e) => {
      setErrorMsg(e.response.data.message || 'Something went wrong! Please try again later');
    });
  }

  const initiateLoanIfNull = () => {
    if (currentLoanId === null) {
      UserDataService.initiateLoan(userData.id)
      .then((response) => {
        dispatch(setCurrentLoanId(response.id))
      })
      .catch((e) => {
        setErrorMsg(e.response.data.message || 'Something went wrong! Please try again later');
      });
    }
  }

  const submitLoan = () => {
    initiateLoanIfNull()
    UserDataService.submitLoan(userData.id, {loanData, balanceSheet}, currentLoanId)
    .then((response) => {
      dispatch(setSelectedMenu(1));
    })
    .catch((e) => {
      setErrorMsg(e.response.data.message || 'Something went wrong! Please try again later');
    });
  }

  const getStepContent = (step) => {
    switch (step) {
      case 0:
        return <BusinessDetailsForm loanData={loanData} onClickHandler={getBalanceSheetToReview} onLoad={() => {}}/>;
      case 1:
        return <ReviewBalancesheet balanceSheet={balanceSheet} onClickHandler={submitLoan} onBackClick={handleBack} />;
      default:
        return null;
    }
  };

  React.useEffect(() => {
    if (currentLoanId !== null) {
      UserDataService.getLoan(userData.id, currentLoanId)
      .then((response) => {
        setLoanData(response)
      })
      .catch((e) => {
        setErrorMsg(e.response.data.message || 'Something went wrong! Please try again later');
      });
    }
  }, [currentLoanId]);

  return (
    <div>
      {errorMsg && <div className='error'>{errorMsg}</div>}
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>{getStepContent(activeStep)}</div>
    </div>
  );
};

export default ApplyLoanStepper;
