import * as React from 'react';
import { useForm, FieldError } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import UserDataService from '../services/user';
import crypto from "crypto-js";

const SignupForm = () => {
  const [formError, setFormError] = React.useState('');
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: 'onBlur' });
  const handleRegistration = (userData) => {
    setFormError('');
    const hashedPassword = crypto.SHA256(userData.password).toString();

    UserDataService.create({...userData, password: hashedPassword})
      .then((response) => {
        navigate('/login');
      })
      .catch((e) => {
        const errorMsg = e.message || 'Something went wrong! Please try again later';
        setFormError(errorMsg);
      });
  };

  const registerOptions = {
    name: { required: 'Name is required' },
    email: { required: 'Email is required' },
    password: {
      required: 'Password is required',
      minLength: {
        value: 8,
        message: 'Password must have at least 8 characters',
      },
    },
  };

  return (
    <div className="form-container">
      <h1 className="title">Welcome to KMoneyLender!</h1>
      {formError !== '' && <p className="error-msg">{formError}</p>}
      <form onSubmit={handleSubmit(handleRegistration)}>
        <div>
          <input
            type="text"
            placeholder="Name"
            {...register('name', registerOptions.name)}
          />
          {errors?.name && (
            <p className="error-msg">{(errors.name as FieldError)?.message}</p>
          )}
        </div>
        <div>
          <input
            type="email"
            placeholder="Email"
            {...register('email', registerOptions.email)}
          />
          {errors?.email && (
            <p className="error-msg">{(errors.email as FieldError)?.message}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            {...register('password', registerOptions.password)}
          />
          {errors?.password && (
            <p className="error-msg">
              {(errors.password as FieldError)?.message}
            </p>
          )}
        </div>
        <button>Sign Up</button>
      </form>
    </div>
  );
};
export default SignupForm;
