import React from 'react';
import { useState } from 'react';

export type NavItemProps = {
  title: string;
  itemId: string;
  onClick: () => void;
};

export type SideNavProps = {
  items: NavItemProps[];
};

const Sidebar: React.FC<SideNavProps> = ({ items }) => {
  const [activeItemId, setActiveItemId] = useState('');

  return (
    <div className="sidebar">
          {items.map((item: NavItemProps) => {
            return (
              <div key={item.itemId}
                onClick={() => 
                  {setActiveItemId(item.itemId); item.onClick();}}
                className={`${activeItemId === item.itemId ? 'active' : ''}`}>
                <span>
                  {item.title}
                </span>
              </div>
            );
          })}
    </div>
  );
};

export default Sidebar;
