import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Home from './Home';
import BusinessDetailsForm from './BusinessDetailsForm';

const MainContainer = () => {
  const [selectedMenu, setSelectedMenu] = useState(1);

  const navItems = [
    {
      title: 'Home',
      itemId: '1',
      onClick: () => setSelectedMenu(1) //navigate('/home'),
    },
    {
      title: 'Apply new Business Loan',
      itemId: '2',
      onClick: () => setSelectedMenu(2)//navigate('/applyLoan'),
    },
  ];

  const getComponentToLoad = () => {
    switch(selectedMenu) {
      case 1: return <Home />;
      case 2: return <BusinessDetailsForm />
      default: return <Home />
    }
  }

  return (
    <div>
      <Sidebar items={navItems} />
      <div className="main-content">{getComponentToLoad()}</div>
    </div>
  );
};

export default MainContainer;
