import React from 'react';
import Sidebar from './Sidebar';
import ContentContainer from './ContentContainer';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import { setSelectedMenu } from '../store/userSlice';

const MainContainer = React.memo(() => {
  const dispatch = useAppDispatch();

  const userData = useAppSelector((state) => state.user);
  const selectedMenu = userData.selectedMenu;

  const navItems = [
    {
      title: 'Home',
      itemId: '1',
      onClick: () => dispatch(setSelectedMenu(1))
    },
    {
      title: 'Apply new Business Loan',
      itemId: '2',
      onClick: () => dispatch(setSelectedMenu(2))
    },
  ];

  return (
    <div>
      <Sidebar items={navItems} />
      <ContentContainer selectedMenu={selectedMenu} goToTab={goToTab}/>
    </div>
  );
});

export default MainContainer;
