import React, {useState} from 'react';
import { useForm, FieldError } from 'react-hook-form';
import AccountingProviderService from './services/accountingProvider';
import UserDataService from './services/user';
import { useAppSelector, useAppDispatch } from './store/hooks';


const submitLoan = (userId, loanData) => {
  UserDataService.submitLoan(userId, loanData)
  .then((response) => {
    console.log("success", response)
    // navigate('/home');
  })
  .catch((e) => {
    console.log("error", e);
    const errorMsg = e.response.data.message || 'Something went wrong! Please try again later';
    // setFormError(errorMsg);
    // navigate('/home');
  });
}

const getBalanceSheetToReview = (companyTaxId) => {
  AccountingProviderService.getBalanceSheet(companyTaxId)
  .then((response) => {
    console.log("success", response)
    // navigate('/home');
  })
  .catch((e) => {
    console.log("error", e);
    const errorMsg = e.response.data.message || 'Something went wrong! Please try again later';
    // setFormError(errorMsg);
    // navigate('/home');
  });
}

const Form = () => {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({ mode: 'onBlur' });
  const handleRegistration = (loanData) => {
    const userData = useAppSelector((state) => state.user);
    balanceSheet.length > 0 ? getBalanceSheetToReview(loanData.companyTaxId) : submitLoan(userData.id, loanData);
  };
  const handleError = (errors: {}) => console.log(errors);

  const registerOptions = {
    companyName: { required: 'Company name is required' },
    companyAddress: { required: 'Company address is required' },
    companyTaxId: { required: 'Company Tax Id is required' },
    loanAmount: { required: 'Loan amount is required' },
    position: { required: 'Position is required' },
    accountingProvider: { required: 'Accounting provider is required' }
  };

  const [accountingProviders, setAccountingProviders] = useState([]);
  const [balanceSheet, setBalanceSheet] = useState([]);
  const [isReviewMode, setIsReviewMode] = useState(false);

  React.useEffect(() => {
    AccountingProviderService.getAllProviders()
    .then((response) => {
      setAccountingProviders(response.data);
    })
    .catch((e) => {
      console.log("Could not fetch loans")
    });
  }, []);

  return (
    <div className="loan-form-container">
      <h2 className="title">
        Enter details of business for which loan is required
      </h2>
      <form onSubmit={handleSubmit(handleRegistration, handleError)}>
        <div>
          <input
            type="text"
            placeholder="Company Name"
            readOnly={isReviewMode}
            {...register('companyName', registerOptions.companyName)}
          />
          {errors?.companyName && (
            <p className="error-msg">
              {(errors.companyName as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="textArea"
            placeholder="Company Address"
            {...register('companyAddress', registerOptions.companyAddress)}
          />
          {errors?.companyAddress && (
            <p className="error-msg">
              {(errors.companyAddress as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="number"
            placeholder="Company Tax Id"
            {...register('companyTaxId', registerOptions.companyTaxId)}
          />
          {errors?.companyTaxId && (
            <p className="error-msg">
              {(errors.companyTaxId as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="text"
            placeholder="Position in the Company"
            {...register('companyName', registerOptions.position)}
          />
          {errors?.companyName && (
            <p className="error-msg">
              {(errors.companyName as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="number"
            placeholder="Loan Amount"
            {...register('loanAmount', registerOptions.loanAmount)}
          />
          {errors?.loanAmount && (
            <p className="error-msg">
              {(errors.loanAmount as FieldError)?.message}
            </p>
          )}
        </div>
        {/* <Controller
          name="accountingProvider"
          control={control}
          defaultValue=""
          render={({ field }) => ( */}
          <select {...register('accountingProvider', registerOptions.accountingProvider)}>
            <option value="">Select an accounting provider</option>
            {accountingProviders.map(accountingProvider => (
              <option key={accountingProvider} value={accountingProvider}>{accountingProvider}</option>
            ))}
          </select>
           {/* )} */}
        {/* /> */}
        <button>{balanceSheet.length > 0 ? 'Apply' : 'Request Balance Sheet'}</button>
      </form>
    </div>
  );
};
export default Form;
