import React from 'react';
import { Button } from '@mui/material';
import { Table, TableHead, TableRow, TableCell, TableBody, Paper } from '@mui/material';

const ReviewBalancesheet = ({balanceSheet, onClickHandler, onBackClick}) => {
  const columns: GridColDef[] = [
    { field: 'Year', headerName: 'year', width: 70 },
    { field: 'Month', headerName: 'month', width: 70 },
    { field: 'Assets Value', headerName: 'Last name', width: 130 },
    { field: 'Profit/Loss', headerName: 'profitOrLoss', width: 130 }
  ];

  return (
    <div className='balance-sheet-container'>
    <Table component={Paper} sx={{ maxWidth: 650 }}>
      <TableHead>
        <TableRow>
          <TableCell>Year</TableCell>
          <TableCell>Month</TableCell>
          <TableCell>Assets Value</TableCell>
          <TableCell>Profit/Loss</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {balanceSheet.map((data) => (
          <TableRow key={data.year}>
            <TableCell>{data.year}</TableCell>
            <TableCell>{data.month}</TableCell>
            <TableCell>{data.assetsValue}</TableCell>
            <TableCell>{data.profitOrLoss}</TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>

    <DataGrid
        rows={balanceSheet}
        columns={columns}
        initialState={{
          pagination: {
            paginationModel: { page: 0, pageSize: 5 },
          },
        }}
        pageSizeOptions={[5, 10]}
        checkboxSelection
      />
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button onClick={onBackClick}>Back</Button>
              <Button variant="contained" onClick={onClickHandler}>Submit</Button>
            </div>
    </div>
  );
};
export default ReviewBalancesheet;
