import React, {useState} from 'react';
import { useForm, FieldError, Controller } from 'react-hook-form';
import AccountingProviderService from './services/accountingProvider';

const Form = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: 'onBlur' });
  const handleRegistration = (data: {}) => console.log(data);
  const handleError = (errors: {}) => console.log(errors);

  const registerOptions = {
    companyName: { required: 'Company name is required' },
    companyAddress: { required: 'Company address is required' },
    companyTaxId: { required: 'Company Tax Id is required' },
    loanAmount: { required: 'Loan amount is required' },
    position: { required: 'Position is required' }
  };

  const [accountingProviders, setAccountingProviders] = useState([]);

  React.useEffect(() => {
    AccountingProviderService.getAllProviders()
    .then((response) => {
      setAccountingProviders(response.data);
    })
    .catch((e) => {
      console.log("Could not fetch loans")
    });
  }, []);

  return (
    <div className="loan-form-container">
      <h2 className="title">
        Enter details of business for which loan is required
      </h2>
      <form onSubmit={handleSubmit(handleRegistration, handleError)}>
        <div>
          <input
            type="text"
            placeholder="Company Name"
            {...register('companyName', registerOptions.companyName)}
          />
          {errors?.companyName && (
            <p className="error-msg">
              {(errors.companyName as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="textArea"
            placeholder="Company Address"
            {...register('companyAddress', registerOptions.companyAddress)}
          />
          {errors?.companyAddress && (
            <p className="error-msg">
              {(errors.companyAddress as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="number"
            placeholder="Company Tax Id"
            {...register('companyTaxId', registerOptions.companyTaxId)}
          />
          {errors?.companyTaxId && (
            <p className="error-msg">
              {(errors.companyTaxId as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="text"
            placeholder="Position in the Company"
            {...register('companyName', registerOptions.position)}
          />
          {errors?.companyName && (
            <p className="error-msg">
              {(errors.companyName as FieldError)?.message}
            </p>
          )}
        </div>
        <div>
          <input
            type="number"
            placeholder="Loan Amount"
            {...register('loanAmount', registerOptions.loanAmount)}
          />
          {errors?.loanAmount && (
            <p className="error-msg">
              {(errors.loanAmount as FieldError)?.message}
            </p>
          )}
        </div>
        <button>Apply</button>
      </form>
    </div>
  );
};
export default Form;
