import { LoginData, User, Loan } from "../typings";
import http, { AxiosResponse } from "./http-common";

class UserDataService {
  getAll() {
    return http.get<User[]>("/users");
  }

  get(id: string) {
    return http.get<User>(`/users/${id}`);
  }

  create(data: User) {
    return http.post<User>("/users/create", data);
  }

  login(data: LoginData) {
    return http.post<AxiosResponse<{ id: string }>>("/users/login", data);
  }

  getLoans(userId: string) {
    return http.get<Loan[]>(`/users/${userId}/loans`);
  }

  getLoan(userId: string, loanId: string) {
    return http.get<Loan>(`/users/${userId}/loans/${loanId}`);
  }

  initiateLoan(id: string) {
    return http.post<AxiosResponse<{ id: string }>>(`/users/${id}/loans`);
  }

  submitLoan(id: string, data: { loanData: Loan; balanceSheet: any[] }, loanId: string | null) {
    if (loanId === null) {
      return http.post<AxiosResponse<{ id: string }>>(`/users/${id}/loans`, data);
    } else {
      return http.post<AxiosResponse<{ id: string }>>(`/users/${id}/loans/${loanId}`, data);
    }
  }
}

export default new UserDataService();
