import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import UserDataService from '../services/user';
import { setLoans, setCurrentLoanId } from '../store/userSlice';
import Approved from '../assets/approved.png';
import Rejected from '../assets/rejected.png';
import Edit from '../assets/edit.png';

const Home = ({goToTab}) => {
  const navigate = useNavigate();
  const dispatch = useAppDispatch();

  const userData = useAppSelector((state) => state.user);
  const loans = userData.loans;

  const editLoan = (loanId) => {
    dispatch(setCurrentLoanId(loanId));
    goToTab(2);
  }

  React.useEffect(() => {
    dispatch(setCurrentLoanId(null))

    if (!userData.isLoggedIn) {
      navigate('/login');
    } else {
      UserDataService.getLoans(userData.id)
      .then((response) => {
        dispatch(setLoans(response.data))
        navigate('/home');
      })
      .catch((e) => {
        console.log("Could not fetch loans")
      });
    }
  }, []);

  return (
    <div>
      {loans.length > 0 ?
        loans.map((loan) => (
          <div key={loan.id} className="snapshot">
            <p className="title">{loan.companyName}
            {loan.status === 'Draft' && <img src={Edit} onClick={() => editLoan(loan.id)}/>}
            </p>
            <div className="content">
              <p><span className='label'>Amount: </span>{loan.loanAmount}</p>
              <img src={loan.status === 'Approved' ? Approved : Rejected} />
            </div>
            <p className='footer'><span className='label'>Status: </span>{loan.status}</p>
          </div>
        )) :
        <p>No loans applied yet! Get started <span className='clickable' onClick={() => goToTab(2)}>here!</span></p>}
    </div>
  );
};

export default Home;
