import React from 'react';
import { useForm, FieldError } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import UserDataService from '../services/user';
import crypto from "crypto-js";
import { loginUser } from '../store/userSlice';
import { useAppDispatch } from '../store/hooks';

const LoginForm = () => {
  const [formError, setFormError] = React.useState('');
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: 'onBlur' });
  const handleRegistration = (userData) => {
    setFormError('');
    const hashedPassword = crypto.SHA256(userData.password).toString();

    UserDataService.login({...userData, password: hashedPassword})
      .then((response) => {
        dispatch(loginUser({id: response.data.id}))
        navigate('/home');
      })
      .catch((e) => {
        setFormError('Invalid email or password!');
      });
  };

  const registerOptions = {
    email: { required: 'Email is required' },
    password: {
      required: 'Password is required',
      minLength: {
        value: 8,
        message: 'Password must have at least 8 characters',
      },
    },
  };

  const goToSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="form-container">
      <h1 className="title">Welcome to KMoneyLender!</h1>
      {formError !== '' && <p className="error-msg">{formError}</p>}
      <form onSubmit={handleSubmit(handleRegistration)}>
        <div>
          <input
            type="email"
            placeholder="Email"
            {...register('email', registerOptions.email)}
          />
          {errors?.email && (
            <p className="error-msg">{(errors.email as FieldError)?.message}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            {...register('password', registerOptions.password)}
          />
          {errors?.password && (
            <p className="error-msg">
              {(errors.password as FieldError)?.message}
            </p>
          )}
        </div>
        <button>Login</button>
        <p className="signup-prompt">
          New user? Sign up <span onClick={goToSignup}>here!</span>
        </p>
      </form>
    </div>
  );
};
export default LoginForm;
